+"use client";
+import {
+  createUserWithEmailAndPassword,
+  onAuthStateChanged,
+  sendEmailVerification,
+} from "firebase/auth";
+import { useRouter } from "next/navigation";
+import { useEffect, useState } from "react";
+import { auth, db } from "../firebase";
+import { getFirestore, collection, addDoc, serverTimestamp, doc, setDoc } from "firebase/firestore";
+import { ToastContainer, toast } from "react-toastify";
+import { uuid } from "uuidv4";
+import '../globals.css';
+import Image from 'next/image';
+
+
+export default function signUp() {
+  const [email, setEmail] = useState("");
+  const [password, setPassword] = useState("");
+  const [passwordAgain, setPasswordAgain] = useState("");
+  const [firstName, setFirstName] = useState("");
+  const [userName, setUserName] = useState("");
+  const [lastName, setLastName] = useState("");
+  const [currentUser, setCurrentUser] = useState(null); 
+  const router = useRouter();
+
+  const [isMobile, setIsMobile] = useState(false);
+
+  useEffect(() => {
+    setIsMobile(window.innerWidth <= 768);
+    
+    const handleResize = () => {
+      setIsMobile(window.innerWidth <= 768);
+    };
+    
+    window.addEventListener('resize', handleResize);
+
+    return () => {
+      window.removeEventListener('resize', handleResize);
+    };
+
+  }, []);
+
+  const crypto = require("crypto");
+  
+  const signup = async () => {
+    try {
+    
+      const authUser = await createUserWithEmailAndPassword(auth, email, password);
+  
+      const salt = crypto.randomBytes(16).toString("hex");
+      const hashedPassword = crypto
+        .pbkdf2Sync(password, salt, 1000, 64, "sha512")
+        .toString("hex");
+
+      const user_id = authUser.user.uid;
+      const docId = uuid()
+
+      const userRef = doc(db, "users", docId ); 
+    
+      await setDoc(userRef, {
+        firstName,
+        lastName,
+        email,
+        hashedPassword,
+        userName,
+        isEmailVerified: false,
+        createdAt: serverTimestamp(),
+        userId: user_id
+      });
+
+      // await sendEmailVerification(auth.currentUser);
+
+      alert("Account created");
+  
+      await new Promise((resolve) => setTimeout(resolve, 6500));
+  
+      router.push("signIn");
+    } catch (error) {
+      console.error("Error:", error);
+      toast.error(error.message, {
+        className: "custom-toast-error",
+      });
+    }
+  };
+  
+
+
+  return (
+    <div className="flex min-h-screen items-center justify-center background">
+      <div className="w-full max-w-xl mx-auto flex rounded-3xl overflow-hidden joinRellyBg">
+        <div className="w-1/2 p-8 text-center">
+          <Image
+            src="/assets/relly_wink_pointing_right.png"
+            alt="Image"
+            width={isMobile ? 200 : 250}
+            height={isMobile ? 200 : 250}
+            className='rellyImg rounded-lg'
+          />
+          <p className="text-2xl font-extrabold mt-4">Join Relly</p>
+          <p className="text-lg  mt-2 font-bold">Your Trusted Relationship Guide</p>
+        </div>
+        <div className="w-1/2 p-8 formRellyBg rounded-3xl">
+          <h2 className="text-2xl font-bold leading-9 tracking-tight mb-6 text-center">Get Started</h2>
+          <div className="space-y-4">
+            <input
+              id="firstName"
+              name="firstName"
+              type="text"
+              placeholder="First Name"
+              onChange={(e) => setFirstName(e.target.value)}
+              required
+              className="block w-full border-gray-300 py-2 px-3 text-gray-800 shadow-sm rounded sm:text-sm"
+            />
+            <input
+              id="lastName"
+              name="lastName"
+              type="text"
+              placeholder="Last Name"
+              onChange={(e) => setLastName(e.target.value)}
+              required
+              className="block w-full rounded border-gray-300 py-2 px-3 text-gray-800 shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
+            />
+            <input
+              id="email"
+              name="email"
+              type="email"
+              placeholder="Email address"
+              autoComplete="email"
+              onChange={(e) => setEmail(e.target.value)}
+              required
+              className="block w-full rounded border-gray-300 py-2 px-3 text-gray-800 shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
+            />
+            <input
+              id="password"
+              name="password"
+              type="password"
+              placeholder="Password"
+              autoComplete="current-password"
+              onChange={(e) => setPassword(e.target.value)}
+              required
+              className="block w-full rounded border-gray-300 py-2 px-3 text-gray-800 shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
+            />
+            <input
+              id="passwordAgain"
+              name="passwordAgain"
+              type="password"
+              placeholder="Password Again"
+              autoComplete="current-password"
+              onChange={(e) => setPasswordAgain(e.target.value)}
+              required
+              className="block w-full rounded border-gray-300 py-2 px-3 text-gray-800 shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
+            />
+            <button
+              disabled={
+                !email ||
+                !password ||
+                !passwordAgain ||
+                password !== passwordAgain
+              }
+              onClick={() => signup()}
+              className="w-full bg-indigo-500 py-2 px-4 text-white font-semibold rounded-md hover:bg-indigo-400 focus:ring-indigo-500 focus:ring-offset-indigo-200 focus:ring-2 focus:ring-offset-2"
+            >
+              Sign Up
+            </button>
+          </div>
+          <p className="mt-4 text-center text-sm text-gray-500">
+            Already started?{" "}
+            <button
+              onClick={() => router.push("signIn")}
+              className="font-semibold leading-6 text-indigo-500 hover:underline"
+            >
+              Sign In
+            </button>
+          </p>
+        </div>
+      </div>
+    </div>
+  )
+}
+
